svg.script:
function row(svg, y, name, pct, grad, width){
  const g = document.createElementNS('http://www.w3.org/2000/svg','g');
  g.setAttribute('transform','translate(36,'+y+')');

  const n = document.createElementNS(svg.namespaceURI,'text');
  n.setAttribute('class','name'); n.setAttribute('x','0'); n.setAttribute('y','12');
  n.textContent = name; g.appendChild(n);

  const p = document.createElementNS(svg.namespaceURI,'text');
  p.setAttribute('class','pct'); p.setAttribute('x','360'); p.setAttribute('y','12'); p.setAttribute('text-anchor','end');
  p.textContent = pct + '%'; g.appendChild(p);

  const use = document.createElementNS(svg.namespaceURI,'use');
  use.setAttributeNS('http://www.w3.org/1999/xlink','href','#bar');
  use.setAttribute('x','0'); use.setAttribute('y','18'); g.appendChild(use);

  // colorear y animar
  const fill = document.createElementNS(svg.namespaceURI,'rect');
  fill.setAttribute('x','0'); fill.setAttribute('y','22'); fill.setAttribute('height','8'); fill.setAttribute('rx','4');
  fill.setAttribute('fill','url(#'+grad+')'); fill.setAttribute('width','0');
  use.after(fill);

  const shineCover = document.createElementNS(svg.namespaceURI,'rect');
  shineCover.setAttribute('x','0'); shineCover.setAttribute('y','22'); shineCover.setAttribute('height','8'); shineCover.setAttribute('rx','4');
  shineCover.setAttribute('fill','#fff'); shineCover.setAttribute('opacity','.18');
  shineCover.setAttribute('mask','url(#m-shine)'); shineCover.setAttribute('width', '0');
  fill.after(shineCover);

  // animaciones
  const a1 = document.createElementNS(svg.namespaceURI,'animate');
  a1.setAttribute('attributeName','width'); a1.setAttribute('from','0'); a1.setAttribute('to', width);
  a1.setAttribute('dur','0.9s'); a1.setAttribute('fill','freeze'); fill.appendChild(a1);

  const a2 = document.createElementNS(svg.namespaceURI,'animate');
  a2.setAttribute('attributeName','width'); a2.setAttribute('from','0'); a2.setAttribute('to', width);
  a2.setAttribute('dur','0.9s'); a2.setAttribute('fill','freeze'); shineCover.appendChild(a2);

  const sr = document.getElementById('shineRect').cloneNode(true);
  const a3 = document.createElementNS(svg.namespaceURI,'animate');
  a3.setAttribute('attributeName','x'); a3.setAttribute('from','-90'); a3.setAttribute('to','360');
  a3.setAttribute('dur','2.4s'); a3.setAttribute('repeatCount','indefinite');
  sr.appendChild(a3);
  // insertar una copia del brillo dentro del grupo
  g.appendChild(sr);

  svg.appendChild(g);
}

const svg = document.documentElement;
// width = porcentaje * 3.6 (porque barra total = 360)
row(svg,  80, 'JavaScript', 90, 'g-js',  324);
row(svg, 130, 'TypeScript', 78, 'g-ts',  281);
row(svg, 180, 'HTML',       72, 'g-html',259);
row(svg, 230, 'CSS',        69, 'g-css', 248);
row(svg, 280, 'Python',     88, 'g-py',  317);
row(svg, 330, 'R',          84, 'g-r',   302);
row(svg, 380, 'SQL',        80, 'g-sql', 288);
row(svg, 430, 'Docker',     78, 'g-dk',  281);
// Ejemplo extra:
// row(svg, 480, 'TensorFlow', 70, 'g-tf', 252);
